import Layout from '../../components/MyLayout';
import fetch from 'isomorphic-unfetch';

const Post = props => (
  <Layout>
    <h1>{props.show.name}</h1>
    <h3>PROFIL MAHASISWA STIKOM PGRI BANYUWANGI</h3>
        <h4>Nama : Aditya Nur' Aini<br/>
        Tempat,Tgl Lahir : Banyuwangi, 04 Agustus 2000<br/>
        Alamat : JL. Raya Lijen No.135 Desa Olehsari<br/>
        Pekerjaan : Mahasiswa/ Usaha Online Shop<br/>
        Hobi : Membaca<br/>
        NIM : 1118101568<br/>
        JURUSAN : S1 TEKNIK INFORMATIKA - MALAM 2018
        </h4>
  </Layout>
);

Post.getInitialProps = async function(context) {
  const { id } = context.query;
  const res = await fetch(`https://api.tvmaze.com/shows/${id}`);
  const show = await res.json();

  console.log(`Fetched show: ${show.name}`);

  return { show };
};

export default Post;
